mapper.insert:
		INSERT into Cart (

		)VALUES(
			  #{userNum}
			, #{productNum}
			, #{branchNum}
			, #{shotCustom}
			, (SELECT 
                    (p.PRICE+s.PRICE)
                FROM
                    PRODUCT p
                    , SHOTCUSTOM s
                WHERE
                    p.PRODUCT_NUM = #{productNum}
                AND
                    s.SHOT_CUSTOM = #{shotCustom})
				)

mapper.update:
		UPDATE 
			CART
		SET 
			QTY = #{qty}
			, TOTAL_PRICE= 	
				(SELECT 
					(p.PRICE+s.PRICE)*#{qty}
				FROM
					PRODUCT p
					, SHOTCUSTOM s
				WHERE
					p.PRODUCT_NUM = #{productNum}
 				AND
					s.SHOT_CUSTOM = #{shotCustom})
			, INSERT_TIME=CURRENT_DATE
		WHERE 
			USER_NUM = #{userNum} 
		AND 
			PRODUCT_NUM = #{productNum}
		AND 
			BRANCH_NUM = #{branchNum}
		AND 
			SHOT_CUSTOM = #{shotCustom}

mapper.select:
SELECT
	QTY
FROM
	CART
WHERE 
	USER_NUM = #{userNum} 
AND 
	PRODUCT_NUM = #{productNum}
AND 
	BRANCH_NUM = #{branchNum}
AND 
	SHOT_CUSTOM = #{shotCustom}

mapper.delete:
DELETE FROM CART
WHERE 
	USER_NUM = #{userNum} 
AND 
	PRODUCT_NUM = #{productNum}
AND 
	BRANCH_NUM = #{branchNum}
AND 
	SHOT_CUSTOM = #{shotCustom}

mapper.select:
	   SELECT *
        FROM
            CART
        WHERE 
            USER_NUM = #{userNum} 
        AND 
            PRODUCT_NUM = #{productNum}
        AND 
            BRANCH_NUM = #{branchNum}
        AND 
            SHOT_CUSTOM = #{shotCustom}
