mapper.select:
SELECT count(id) 
  FROM MEMBER 
 WHERE email = #{email}
   AND id = #{id}

mapper.update:
UPDATE MEMBER
   SET pw = #{pw}
 WHERE id = #{id}

mapper.select:
SELECT id 
  FROM MEMBER 
 WHERE email = #{email}
   AND name = #{name}

SELECT count(id)
  FROM MEMBER 
 WHERE id = #{id}

SELECT name, id, pw, tel, email, TO_CHAR(BIRTH,'YYYY-MM-DD'), authority, stamp, delete_yn, branch_num
  FROM MEMBER
 WHERE user_num = #{userNum}

mapper.update:
UPDATE MEMBER
   SET name = #{name}
		, tel = #{tel}
		, email = #{email}
 WHERE user_num = #{userNum}

UPDATE MEMBER
   SET delete_yn = 'Y'
 WHERE user_num = #{userNum}
