mapper.select:
SELECT name, id, pw, tel, email, birth
  FROM MEMBER
 WHERE user_num = #{userNum}

mapper.update:
UPDATE MEMBER
   SET name = #{name}
		, id = #{id}
		, pw = #{pw}
		, tel = #{tel}
		, email = #{email}
		, birth = #{birth}
 WHERE user_num = #{userNum}

UPDATE MEMBER
   SET delete_yn = 'Y'
 WHERE user_num = #{userNum}

mapper.select:
SELECT count(id)
  FROM MEMBER 
 WHERE id = #{id}

SELECT USER_NUM, ID, PW, NAME, TEL, EMAIL, BIRTH, AUTHORITY, STAMP, DELETE_YN
  FROM MEMBER
 WHERE ID = #{id}
